import React, { useState, useEffect } from "react";
import "./App.css";
import Post from "./Post";
import { db ,auth } from "./firebase";
import ImageUpload from "./ImageUpload";
import { Modal , Button , Input , makeStyles } from "@material-ui/core";

function getModalStyle() {
  const top = 50;
  const left = 50;

  return {
    height: "300px",
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const useStyles = makeStyles((theme) => ({
  paper: {
    position: "absolute",
    width: 400,
    height: 200,
    backgroundColor: theme.palette.background.paper,
    border: "2px solid #000",
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

function App() {

const classes = useStyles();
const [modalStyle] = useState(getModalStyle);
const [posts, setPosts] = useState([]);
const [username, setUsername] = useState("");
const [email, setEmail] = useState("");
const [password, setPassword] = useState("");
const [user, setUser] = useState(null);
const [open, setOpen] = useState(false);
const [registerOpen, setRegisterOpen] = useState(false);

useEffect(() => {
  const unsubscribe = auth.onAuthStateChanged((authUser) => {
    if (authUser) {
      // user is logged in...
      console.log(authUser);
      setUser(authUser);

      if (authUser.displayName) {
        // dont update username
      } else {
        return authUser.updateProfile({
          displayName: username,
        });
      }
    } else {
      setUser(null);
    }
  });

  return () => {
    unsubscribe();
  };
}, [user, username]);

useEffect(() => {
  db.collection("posts")
    .orderBy("timestamp", "desc")
    .onSnapshot((snapshot) =>
      setPosts(snapshot.docs.map((doc) => ({ id: doc.id, post: doc.data() })))
    );
}, []);

const handleLogin = (e) => {
  e.preventDefault();
  auth
    .signInWithEmailAndPassword(email, password)
    .catch((error) => alert(error.message));

  setOpen(false);
};

const handleRegister = (e) => {
  e.preventDefault();
  auth
    .createUserWithEmailAndPassword(email, password)
    .catch((error) => alert(error.message));

  setRegisterOpen(false);
};

return (
  <div className="app">
    <Modal open={open} onClose={() => setOpen(false)}>
      <div style={modalStyle} className={classes.paper}>
        <form className="app__login">
          <center>
            <img
              className="app__headerImage"
              src="https://www.instagram.com/static/images/web/mobile_nav_type_logo.png/735145cfe0a4.png"
              alt=""
            />
          </center>

          <Input
            placeholder="email"
            type="text"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <Input
            placeholder="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <Button onClick={handleLogin}>Login</Button>
        </form>
      </div>
    </Modal>

    <Modal open={registerOpen} onClose={() => setRegisterOpen(false)}>
      <div style={modalStyle} className={classes.paper}>
        <form className="app__login">
          <center>
            <img
              className="app__headerImage"
              src="https://www.instagram.com/static/images/web/mobile_nav_type_logo.png/735145cfe0a4.png"
              alt=""
            />
          </center>
          <Input
            type="text"
            placeholder="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <Input
            placeholder="email"
            type="text"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <Input
            placeholder="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <Button onClick={handleRegister}>Register</Button>
        </form>
      </div>
    </Modal>

      <div className='app_header'>
        <img
          className='header_image'
          src="https://www.instagram.com/static/images/web/mobile_nav_type_logo.png/735145cfe0a4.png"
          alt=""
        />
      </div>

      <h1>Welcome to the instagram clone with react </h1>

      {
        posts.map(({ id, post }) => (
          //instead of refreshing the whole project the KEY simply loads the new file that you add in the firestore database
          <Post key={id} username={post.username} caption={post.caption} imageUrl={post.imageUrl} />
        ))

      }

    </div>
  );
}

export default App;
