import { Button } from '@material-ui/core';
import React from 'react'
import { useState } from 'react';
import { storage , db } from 'firebase';
import firebase from 'firebase';

function ImageUpload() {
    const [image ,setImage] = useState(null);
    const [progress , setProgress] =useState(0);
    const [caption , setCaption] = useState('');

    const handleChange = (e) => {
        if(e.target.files[0]) {
            setImage(e.target.files[0]);
        }
    };

    const handleUpload =() => {
        const uploadTask = storage.ref(`images/${image.name}`).put(image);
        uploadTask.on(
            "state_changed",
            (snapshot) => {
                // progress function
                const progress = Math.round(
                    (snapshot.bytesTransferred / snapshot.totalBytes) * 100
                    );
                    setProgress(progress);
            },
            (error) => {
                //if there is any error
                console.log(error);
                alert(error.message);
            },
            () =>{
                //complete upload function
                storage
                .ref("images")
                .child(image.name)
                .getDownloadUrl()
                //allow us to get the download link of the image
                .then(url => {
                    //post image inside the database

                    db.collection("posts").add({
                        timestamp: firebase.firestore.FieldValue.serverTimestamp(),
                        caption: caption,
                        imageUrl: url,
                        //username: username,

                      });

                      //reset the bar again on the same state
                      setProgress(0);
                      setCaption("");
                      setImage(null);

                })
            }
        )
    };

  return (


    <div>



  <progress value={progress} max />
  <input type="text" placeholder =" Enter the Caption" onChange={event => setCaption(event.target.value)}  value ={caption} />
  <input type="file" onChange={handleChange} />
  <Button onClick ={handleUpload} >
     Upload
  </Button>







    </div>
  )
}

export default ImageUpload ;
