import React, { useState, useEffect } from "react";
import "./App.css";
import Post from "./Post";
//import ImageUpload from "./ImageUpload";
import { db, auth } from "./firebase";
import { Button, Modal, makeStyles ,Input } from "@material-ui/core";
//import FlipMove from "react-flip-move";
//import InstagramEmbed from "react-instagram-embed";

function getModalStyle() {
  const top = 50;
  const left = 50;

  return {
    height: "300px",
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const useStyles = makeStyles((theme) => ({
  paper: {
    position: "absolute",
    width: 400,
    height: 200,
    backgroundColor: theme.palette.background.paper,
    border: "2px solid #000",
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));


function App() {
  const classes = useStyles();
  const [ modalStyle ] = useState(getModalStyle);
  const [posts, setPosts] = useState([]);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState(null);
  const [open, setOpen] = useState(false);
  const [registerOpen, setRegisterOpen] = useState(false);



  useEffect(() => {
      const vermaji =  auth.onAuthStateChanged((authUser) =>{
          if(authUser) {
            // if you logged in
            console.log(authUser);
            setUser(authUser);

            if (authUser.displayName) {
              // dont update username
            } else {
              //if we just created a username 
              return authUser.updateProfile({
                displayName: username,
              });
            }

          } 
          else
          { 
            // if you logged out
            setUser(null);
          }
        })
        return() => {
          //perform some clean up
          vermaji(); 
        }
      }, [ user , username ]);


  useEffect(() => {
    //here the code runs 
    db.collection('posts').onSnapshot(snapshot => {
      // snapshot whenever a new file come it will update the firebase
      setPosts(snapshot.docs.map(doc => ({
        id: doc.id,
        post: doc.data()
      })))
    }) 
  }, []);

  const signUp= (event) =>{
    event.preventDefault();
    auth.createUserWithEmailAndPassword(email ,password)
    .then
    .catch((error) => alert(error.message));
  }

  return (
    <div className="app">
      <Modal 
      open={open} 
      onClose= {() => setOpen(false)}
      >
        <div style={modalStyle} className={classes.paper}>
          <form className="app_login">
            <center>
              <img
                className="app__headerImage"
                src="https://www.instagram.com/static/images/web/mobile_nav_type_logo.png/735145cfe0a4.png"
                alt=""
              />
            </center>

            <Input
              placeholder="Username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />

            <Input
              placeholder="E-mail"
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <Input
              placeholder="Password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button type = "submit"onClick={signUp}>Login</Button>
          </form>
        </div>

      </Modal>
      <div className='app_header'>
        <img
          className='header_image'
          src="https://www.instagram.com/static/images/web/mobile_nav_type_logo.png/735145cfe0a4.png"
          alt=""
        />
      </div>

      <Button onClick ={() => setOpen(true)}> Sign Up</Button>

      <h1>Welcome to the instagram clone with react </h1>

      {
        posts.map(({ id, post }) => (
          //instead of refreshing the whole project the KEY simply loads the new file that you add in the firestore database
          <Post key={id} username={post.username} caption={post.caption} imageUrl={post.imageUrl} />
        ))

      }

    </div>
  );
}

export default App;
